document.querySelector(".hamburger").addEventListener("click",()=>{document.querySelector(".nav-links").classList.toggle("expanded")});const t=(()=>typeof localStorage<"u"&&localStorage.getItem("theme")?localStorage.getItem("theme"):window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light")();t==="light"?document.documentElement.classList.remove("dark"):document.documentElement.classList.add("dark");window.localStorage.setItem("theme",t);const o=()=>{const e=document.documentElement;e.classList.toggle("dark");const l=e.classList.contains("dark");localStorage.setItem("theme",l?"dark":"light")};document.getElementById("themeToggle").addEventListener("click",o);
